def run_menu_function(func, loading_message):
    """Wrapper to handle menu function execution with error handling."""
    try:
        show_progress(loading_message, 1)
        func()
    except Exception as e:
        print(f"\n{Fore.YELLOW} {Fore.LIGHTRED_EX}An error occurred: {str(e)}{Style.RESET_ALL}")
        if confirm_action("Would you like to return to the main menu?"):
            return
        else:
            print(center_text(f"\n{Fore.YELLOW}Exiting due to error{Style.RESET_ALL}"))
            sys.exit(1)

def main():
    """Main program loop."""
    try:
        while True:
            choice = main_menu()
            if choice.lower() == "x":
                if confirm_action("Are you sure you want to exit?"):
                    break

            menu_actions = {
                "1": (generate_config, "Preparing config generator"),
                "2": (backup_restore_menu, "Loading backup menu"),
                "3": (manage_ports, "Loading port management"),
                "4": (utilities_menu, "Loading utilities"),
                "5": (port_summary_menu, "Loading port summary")
            }

            if choice in menu_actions:
                func, message = menu_actions[choice]
                run_menu_function(func, message)

    except KeyboardInterrupt:
        print(f"\n\n{Fore.YELLOW}Received interrupt signal, shutting down...{Style.RESET_ALL}")
    except Exception as e:
        print(f"\n{Fore.YELLOW} {Fore.LIGHTRED_EX}Fatal error: {str(e)}{Style.RESET_ALL}")
    finally:
        print(center_text(f"\n{Fore.YELLOW}Thank you for using WireGuard Management{Style.RESET_ALL}"))
        print(create_border())

